$(function(){module("bootstrap-buttons");test("should provide no conflict",function(){var t=$.fn.button.noConflict();ok(!$.fn.button,"button was set back to undefined (org value)");$.fn.button=t});test("should be defined on jquery object",function(){ok($(document.body).button,"button method is defined")});test("should return element",function(){ok($(document.body).button()[0]==document.body,"document.body returned")});test("should return set state to loading",function(){var t=$('<button class="btn" data-loading-text="fat">mdo</button>');equals(t.html(),"mdo","btn text equals mdo");t.button("loading");equals(t.html(),"fat","btn text equals fat");stop();setTimeout(function(){ok(t.attr("disabled"),"btn is disabled");ok(t.hasClass("disabled"),"btn has disabled class");start()},0)});test("should return reset state",function(){var t=$('<button class="btn" data-loading-text="fat">mdo</button>');equals(t.html(),"mdo","btn text equals mdo");t.button("loading");equals(t.html(),"fat","btn text equals fat");stop();setTimeout(function(){ok(t.attr("disabled"),"btn is disabled");ok(t.hasClass("disabled"),"btn has disabled class");start();stop()},0);t.button("reset");equals(t.html(),"mdo","btn text equals mdo");setTimeout(function(){ok(!t.attr("disabled"),"btn is not disabled");ok(!t.hasClass("disabled"),"btn does not have disabled class");start()},0)});test("should toggle active",function(){var t=$('<button class="btn">mdo</button>');ok(!t.hasClass("active"),"btn does not have active class");t.button("toggle");ok(t.hasClass("active"),"btn has class active")});test("should toggle active when btn children are clicked",function(){var t=$('<button class="btn" data-toggle="button">mdo</button>'),s=$("<i></i>");t.append(s).appendTo($("#qunit-fixture"));ok(!t.hasClass("active"),"btn does not have active class");s.click();ok(t.hasClass("active"),"btn has class active")});test("should toggle active when btn children are clicked within btn-group",function(){var t=$('<div class="btn-group" data-toggle="buttons-checkbox"></div>'),s=$('<button class="btn">fat</button>'),a=$("<i></i>");t.append(s.append(a)).appendTo($("#qunit-fixture"));ok(!s.hasClass("active"),"btn does not have active class");a.click();ok(s.hasClass("active"),"btn has class active")});test("should check for closest matching toggle",function(){var t=$("<div data-toggle='buttons-radio'></div>"),s=$("<button class='btn active'></button>"),a=$("<button class='btn'></button>"),e=$("<div></div>");e.append(s,a);t.append(e).appendTo($("#qunit-fixture"));ok(s.hasClass("active"),"btn1 has active class");ok(!a.hasClass("active"),"btn2 does not have active class");a.click();ok(!s.hasClass("active"),"btn1 does not have active class");ok(a.hasClass("active"),"btn2 has active class")})});